import { mdx } from '@mdx-js/react';
/* @jsx mdx */

export const _frontmatter = {
  "title": "Styling MDX Content"
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const Note = makeShortcode("Note");
const layoutProps = {
  _frontmatter
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "styling-mdx-content"
    }}>{`Styling MDX Content`}</h1>
    <p>{`The `}<inlineCode parentName="p">{`theme.styles`}</inlineCode>{` object is the primary way to style content in MDX documents.
This allows you to add typographic styles to markdown without the need to pollute the global scope.
Styles within this object have access to other values in the theme object.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js"
      }}>{`// example theme styles
{
  colors: {
    text: '#000',
    background: '#fff',
    primary: '#07c',
  },
  fonts: {
    body: 'system-ui, sans-serif',
    heading: 'Georgia, serif',
  },
  fontWeights: {
    body: 400,
    heading: 700,
  },
  styles: {
    h1: {
      fontSize: 32,
      fontFamily: 'heading',
      fontWeight: 'heading',
      color: 'primary',
      mt: 4,
      mb: 2,
    },
  }
}
`}</code></pre>
    <p>{`Any MDX document rendered as a child of the `}<inlineCode parentName="p">{`ThemeProvider`}</inlineCode>{` will pick up the styles defined in the theme.`}</p>
    <Note mdxType="Note">
      <p>{`These styles will `}<strong parentName="p">{`not`}</strong>{` apply to HTML elements used outside of MDX. To reuse these styles outside of MDX, see the `}<a parentName="p" {...{
          "href": "/styled"
        }}>{`Styled component`}</a>{` docs.`}</p>
    </Note>
    <h2 {...{
      "id": "typographyjs"
    }}>{`Typography.js`}</h2>
    <p>{`To use `}<a parentName="p" {...{
        "href": "https://github.com/KyleAMathews/typography.js"
      }}>{`Typography.js`}</a>{` themes with Theme UI, install the `}<inlineCode parentName="p">{`@theme-ui/typography`}</inlineCode>{` package and any Typography.js theme.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-sh"
      }}>{`npm i @theme-ui/typography typography-theme-wordpress-2016
`}</code></pre>
    <p>{`Use the `}<inlineCode parentName="p">{`toTheme`}</inlineCode>{` utility to convert the theme for use in Theme UI.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js"
      }}>{`// example theme with Typography.js
import { toTheme } from '@theme-ui/typography'
import wordpress2016 from 'typography-theme-wordpress-2016'
import merge from 'lodash.merge'

const typography = toTheme(wordpress2016)

export default merge(typography, {
  // optional style overrides go here
})
`}</code></pre>
    <p>{`Content in MDX documents will be rendered with styles from the Typography.js theme.
The `}<inlineCode parentName="p">{`toTheme`}</inlineCode>{` function accepts the same `}<a parentName="p" {...{
        "href": "https://github.com/KyleAMathews/typography.js#configuration"
      }}>{`Typography.js configuration options`}</a>{` as the core library,
allowing you to create custom themes with this approach as well.`}</p>
    <Note mdxType="Note">
      <p>{`Theme UI does not currently use colors from Typography.js.`}</p>
    </Note>
    <h2 {...{
      "id": "syntax-highlighting"
    }}>{`Syntax Highlighting`}</h2>
    <p>{`To support syntax highlighting libraries like `}<a parentName="p" {...{
        "href": "https://github.com/PrismJS/prism"
      }}>{`Prism.js`}</a>{`,
colors and other styles can be added to `}<inlineCode parentName="p">{`theme.styles.pre`}</inlineCode>{` to target child elements with class selectors.
Prism.js adds `}<inlineCode parentName="p">{`<span>`}</inlineCode>{` elements with class names that can be used as child selectors.`}</p>
    <p>{`To enable syntax highlighting in MDX, see the `}<a parentName="p" {...{
        "href": "/packages/prism"
      }}><inlineCode parentName="a">{`@theme-ui/prism`}</inlineCode></a>{` package.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js"
      }}>{`{
  colors: {
    gray: '#666',
  },
  styles: {
    pre: {
      '.comment': {
        // theme.colors and other values can be referenced
        color: 'gray',
      }
    }
  }
}
}
`}</code></pre>
    <Note mdxType="Note">
      <p>{`To style content that is not generated with MDX with `}<inlineCode parentName="p">{`theme.styles`}</inlineCode>{`, see the `}<a parentName="p" {...{
          "href": "/api/#base-styles"
        }}><inlineCode parentName="a">{`BaseStyles`}</inlineCode></a>{` component.`}</p>
    </Note>

    </MDXLayout>;
