// gatsby-config.js
const remarkSlug = require('remark-slug')

module.exports = {
  plugins: [
    {
      resolve: 'gatsby-plugin-mdx',
      options: {
        remarkPlugins: [
          remarkSlug,
        ]
      }
    }
  ]
}

// src/gatsby-plugin-theme-ui/components.js
/** @jsx jsx */
import { jsx } from 'theme-ui'

const heading = Tag => props => {
  if (!props.id) return <Tag {...props} />

  return (
    <Tag {...props}>
      <a
        href={`#${props.id}`}>
        {props.children}
      </a>
    </Tag>
  )
}

const components = {
  h1: heading('h1'),
  h2: heading('h2'),
  h3: heading('h3'),
  h4: heading('h4'),
  h5: heading('h5'),
  h6: heading('h6'),
}

export default components

